/* Pin outs
 * I2C to ADC
 * 	PB8 --> SCL
 * 	PB7 --> SDA
 * Debug UART
 * 	PA2 --> TX
 * 	PA3 --> RX
 *
 */


int main(void)
{
	/*initialize the MCU peripherals*/
	clock_init();
	init_leds();
	init_PC6();
	I2C1_Init();
	tim8_output_compare();
	uart2_rxtx_interrupt_init();
	printf("MCU peripheral inits complete \n\r");

	/*initialize the other devices on the board*/
	if(check_IMU() == true){
		printf("IMU responded with correct device ID\n\r");
	}
	init_lsm6dso();


	printf("Hello World\n\r");

	while(1)
	{
		if(rx_buffer_count() > 0){
			process_commands();
		}
	}

}

void USART2_IRQHandler(void)
{
	if(USART2->SR & SR_RXNE){
		uart_rx_callback();
	}
	if(USART2->SR & SR_TXE){
		uart_tx_callback();
	}

}


